import { useState, useEffect } from "react";

function App() {
  const URL = "https://api.coinpaprika.com/v1/tickers";
  const [loading, setLoading] = useState(true);
  fetch(URL)
    .then((response) => response.json())
    .then((json) => {
      console.log(json);
      setLoading(false);
    });
  return (
    <div>
      <h1>The coins</h1>
      {

        {function(){
          if(loading) return(<div>Loading...</div>)
        }}
      }()
    </div>
  );
}

export default App;
